class Student:
def __init__(self, i, n , t):
self.__id = i # private
self.__name = n # private
self.phone = t # public
self.__course = [ ] # private and list
def setid(self, i):
self.__id = i
def getid(self):
return self.__id
def setname(self, n):
self.__name = n
def getname(self):
return self.__name
def register(self, c):
if not (c in self.__course):
self.__course.append(c)
elif (c in self.__course):
print(c + " <==This course is register before")
def __str__(self):
return " id= \t "+str(self.__id)\
+ "\n name: \t " + str(self.__name)\
+ "\n courses: " + str(self.__course)\
+ "\n phone: " + str(self.phone)
def display(self):
print(str(self))
# main code
s1 = Student(111, 'Wael Jafaar', '0778899123' )
s1.register('CS210 oop Py')
s1.register('CS130 OS')
s1.register('CS210L oop Py')
s1.register('CS130 OS') # error
s1.display()

